class IndexND<2> {
 private:
  int x[2], y[2];

 public:
  using Index = IndexND<2>;

  int i, j;
  // int offset;
  int stride;
  Vector2 storage_offset;

  IndexND() {
  }

  IndexND(int x0,
          int x1,
          int y0,
          int y1,
          Vector2 storage_offset = Vector2(0.5f, 0.5f)) {
    x[0] = x0;
    x[1] = x1;
    y[0] = y0;
    y[1] = y1;
    i = x[0];
    j = y[0];
    // offset = 0;
    stride = y[1] - y[0];
    this->storage_offset = storage_offset;
  }

  IndexND(Vector2i start,
          Vector2i end,
          Vector2 storage_offset = Vector2(0.5f, 0.5f)) {
    x[0] = start[0];
    x[1] = end[0];
    y[0] = start[1];
    y[1] = end[1];
    i = x[0];
    j = y[0];
    // offset = 0;
    stride = y[1] - y[0];
    this->storage_offset = storage_offset;
  }

  IndexND(int i, int j) {
    this->i = i;
    this->j = j;
  }

  void next() {
    j++;
    // offset++;
    if (j == y[1]) {
      j = y[0];
      i++;
      if (i == x[1]) {
      }
    }
  }

  Index operator++() {
    this->next();
    return *this;
  }

  bool operator==(const IndexND<2> &o) const {
    return (i == o.i && j == o.j);
  }

  bool operator!=(const IndexND<2> &o) const {
    return !(i == o.i && j == o.j);
  }

  Index &to_end() {
    i = x[1];
    j = y[0];
    // offset = (x[1] - x[0]) * (y[1] - y[0]);
    return *this;
  }

  const Index &operator*() const {
    return *this;
  }

  Index &operator*() {
    return *this;
  }

  int operator[](int c) {
    return *(&i + c);
  }

  int operator[](int c) const {
    return *(&i + c);
  }

  Index neighbour(int di, int dj) const {
    Index i = *this;
    i.i += di;
    i.j += dj;
    return i;
  }

  Index neighbour(Vector2i d) const {
    Index i = *this;
    i.i += d.x;
    i.j += d.y;
    return i;
  }

  Index operator+(Vector2i d) const {
    return neighbour(d);
  }

  Vector2 get_pos() const {
    return Vector2((real)i + storage_offset.x, (real)j + storage_offset.y);
  }

  Vector2i get_ipos() const {
    return Vector2i(i, j);
  }
};

